mapper.select:
select id, member_id, member_password,  member_name, member_email, member_phone, address_id, member_status, member_agree_privacy, member_agree_marketing, created_datetime, updated_datetime
from tbl_member
where member_id = #{memberId}

select id, member_id, member_password,  member_name, member_email, member_phone, address_id, member_status, member_agree_privacy, member_agree_marketing, created_datetime, updated_datetime
from tbl_member
where member_email = #{memberEmail}

mapper.update:
update tbl_member
set address_id = #{id}
where id = #{id}

UPDATE tbl_member SET
    member_name    = #{memberName},
    member_email   = #{memberEmail},
    member_phone   = #{memberPhone}

    , member_password = #{memberPassword}

WHERE id = #{id}
